import React, { useEffect, useState } from 'react';
import './Account.css';

const Account = () => {
  const [accountDetails, setAccountDetails] = useState(null);

  useEffect(() => {
    const fetchAccountDetails = async () => {
      const response = await fetch('/api/account-details');
      const data = await response.json();
      setAccountDetails(data);
    };

    fetchAccountDetails();
  }, []);

  if (!accountDetails) return <div>Loading...</div>;

  return (
    <div className="account-container">
      <div className="order-history">
        <h2>Account</h2>
        <h3>Order history</h3>
        {accountDetails.orders.length === 0 ? (
          <p>You haven't placed any orders yet.</p>
        ) : (
          <ul>
            {accountDetails.orders.map((order, index) => (
              <li key={index}>{order}</li>
            ))}
          </ul>
        )}
        <a href="/logout">Log out</a>
      </div>
      <div className="account-details">
        <h3>Account details</h3>
        <p>{accountDetails.name}</p>
        <p>{accountDetails.country}</p>
        <a href="/addresses">View addresses ({accountDetails.addressCount})</a>
      </div>
    </div>
  );
};

export default Account;
