import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation
import a1_img from '../Components/Assets/a1.png';
import a2_img from '../Components/Assets/a2.png';
import a3_img from '../Components/Assets/a3.png';
import a4_img from '../Components/Assets/a4.png';
import './Anklets.css';
import { WishlistContext } from '../WishlistContext'; // Wishlist context
import { CartContext } from '../CartContext'; // Cart context
import Header from '../Components/Header/Header'; // Corrected import path

const anklets = [
  { id: 1, name: 'Evileye Anklet with Small Beads', image: a1_img, new_price: 130.0, old_price: 80.0 },
  { id: 2, name: 'Multicolour Anklet', image: a2_img, new_price: 130.0, old_price: 80.0 },
  { id: 3, name: 'Evileye Anklet with Large Beads', image: a3_img, new_price: 130.0, old_price: 80.0 },
  { id: 4, name: 'Golden Chain Anklet', image: a4_img, new_price: 150.0, old_price: 100.0 },
];

const Anklets = () => {
  // Access context values
  const { wishlist, addToWishlist, removeFromWishlist } = useContext(WishlistContext);
  const { addToCart } = useContext(CartContext); // Cart context (removed cart destructure)
  const navigate = useNavigate(); // React Router navigation

  const isInWishlist = (anklet) => wishlist.some((item) => item.id === anklet.id); // Check if in wishlist

  // Handle Add to Cart without any restriction
  const handleAddToCart = (anklet) => {
    addToCart(anklet); // Add item to cart
    navigate('/cart'); // Navigate to Cart page
  };

  return (
    <div>
      <Header />
      <h1>Welcome to the Anklets category page!</h1>
      <div className="container">
        {anklets.map((anklet) => (
          <div key={anklet.id} className="anklet-card">
            <div className="anklet-image-container">
              {/* Wishlist Heart Button */}
              <div
                className={`heart-icon ${isInWishlist(anklet) ? 'active' : ''}`}
                onClick={() => {
                  if (isInWishlist(anklet)) {
                    removeFromWishlist(anklet); // Remove from wishlist
                  } else {
                    addToWishlist(anklet); // Add to wishlist
                  }
                }}
              ></div>
              <img className="anklet-image" src={anklet.image} alt={anklet.name} />
            </div>
            <div className="anklet-name">{anklet.name}</div>
            <div className="anklet-price">
              <span className="new-price">${anklet.new_price}</span>{' '}
              <span className="old-price">${anklet.old_price}</span>
            </div>
            {/* Add to Cart Button */}
            <button
              className="add-to-cart-btn"
              onClick={() => handleAddToCart(anklet)}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Anklets;
