import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./ProductDetail.css";

const ProductDetail = () => {
  const { id } = useParams(); // Fetch product ID from the URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`/api/products/${id}`); // Replace with your actual API endpoint
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error("Error fetching product data:", error);
      }
    };

    fetchProduct();
  }, [id]);

  if (!product) {
    return <div className="loading">Loading product details...</div>;
  }

  return (
    <div className="product-details">
      <div className="product-details-container">
        <div className="image-section">
          <img src={product.image} alt={product.name} />
          <div className="thumbnail-container">
            {product.images.map((img, index) => (
              <img key={index} src={img} alt={`Thumbnail ${index}`} className="thumbnail" />
            ))}
          </div>
        </div>
        <div className="info-section">
          <h1>{product.name}</h1>
          <p className="price">
            ₹{product.price} <span className="original-price">₹{product.originalPrice}</span>
          </p>
          <p className="description">{product.description}</p>
          <div className="features">
            <p>
              <strong>Warranty:</strong> {product.warranty}
            </p>
            <p>
              <strong>Material:</strong> {product.material}
            </p>
          </div>
          <div className="additional-info">
            <p>
              <strong>Returns:</strong> Easy 30 Day Return
            </p>
            <p>
              <strong>Plating:</strong> Lifetime Plating
            </p>
          </div>
          <div className="delivery-check">
            <p>Enter your PIN code for delivery info:</p>
            <input type="text" placeholder="Enter 6-digit PIN code" />
            <button className="btn check-btn">Check</button>
          </div>
          <div className="actions">
            <button className="btn buy-now">Buy Now</button>
            <button className="btn add-to-cart">Add to Cart</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
