import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./ProductDetails.css"; // Import the CSS file

const ProductDetails = () => {
  const { id } = useParams(); // Extract the product ID from the URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Mock product data
    const mockData = [
      {
        id: 1,
        name: "Evil Eye Bracelet",
        price: 250,
        originalPrice: 500,
        description: "A stunning bracelet to ward off negativity.",
        image: "https://via.placeholder.com/300",
      },
      {
        id: 2,
        name: "Tropical Rainbow Anklet",
        price: 300,
        originalPrice: 600,
        description: "Bright and colorful anklet for tropical vibes.",
        image: "https://via.placeholder.com/300",
      },
      {
        id: 3,
        name: "Ocean Aura Anklets",
        price: 350,
        originalPrice: 700,
        description: "Beautiful anklet inspired by the ocean.",
        image: "https://via.placeholder.com/300",
      },
    ];

    // Find the product by ID
    const productData = mockData.find((item) => item.id === parseInt(id));
    setProduct(productData);
  }, [id]);

  if (!product) {
    return <div>Loading product details...</div>;
  }

  return (
    <div className="product-details-container">
      <h1>{product.name}</h1>
      <img src={product.image} alt={product.name} className="product-details-image" />
      <p>Price: ₹{product.price}</p>
      <p className="original-price">Original Price: ₹{product.originalPrice}</p>
      <p className="product-description">{product.description}</p>
      <button className="add-to-cart-button">Add to Cart</button>
    </div>
  );
};

export default ProductDetails;
