import React, { useState } from 'react';
import './Navbar.css';
import logo from '../Assets/logo.png'; // Path to your logo
import cart from '../Assets/cart.png'; // Path to your cart icon
import hearticon from '../Assets/heart-logo.png'; // Path to wishlist icon
import { Link } from 'react-router-dom';

export const Navbar = () => {
  const [menu, setMenu] = useState('shop'); // State to manage active menu

  return (
    <div className="navbar">
      {/* Logo Section */}
      <div className="nav-logo">
        <img src={logo} alt="Logo" />
        <p>SHOPPER</p>
      </div>

      {/* Navigation Menu */}
      <ul className="nav-menu">
        <li onClick={() => setMenu('shop')}>
          <Link to="/" className={menu === 'shop' ? 'active' : ''}>
            Shop
          </Link>
          {menu === 'shop' && <hr />}
        </li>
        <li onClick={() => setMenu('mens')}>
          <Link to="/mens" className={menu === 'mens' ? 'active' : ''}>
            Mens
          </Link>
          {menu === 'mens' && <hr />}
        </li>
        <li onClick={() => setMenu('womens')}>
          <Link to="/womens" className={menu === 'womens' ? 'active' : ''}>
            Womens
          </Link>
          {menu === 'womens' && <hr />}
        </li>
        <li onClick={() => setMenu('kids')}>
          <Link to="/kids" className={menu === 'kids' ? 'active' : ''}>
            Kids
          </Link>
          {menu === 'kids' && <hr />}
        </li>
      </ul>

      {/* Icons and Login Section */}
      <div className="nav-icons">
        {/* Wishlist */}
        <div className="nav-icon">
          <img src={hearticon} alt="Wishlist" className="nav-icon-img" />
        </div>

        {/* Cart */}
        <div className="nav-cart">
          <Link to="/cart" className="nav-cart-link">
            <img src={cart} alt="Cart" className="nav-cart-icon" />
            <div className="nav-cart-count">0</div>
          </Link>
        </div>

        {/* Login Button */}
        <Link to="/login">
          <button className="nav-login-btn">Login</button>
        </Link>
      </div>
    </div>
  );
};
