import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginSignup.css';

export const LoginSignup = () => {
  const [isLogin, setIsLogin] = useState(true); // Tracks whether the form is in Login or Sign Up mode
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  const navigate = useNavigate(); // For navigation

  // Toggle between Login and Sign Up forms
  const toggleForm = () => setIsLogin(!isLogin);

  // Handle input changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent the page from reloading

    if (!isLogin && !formData.name) {
      alert('Please enter your name for signup!');
      return;
    }

    if (!formData.email || !formData.password) {
      alert('Please fill all the fields!');
      return;
    }

    const url = isLogin
      ? 'http://localhost:4000/login' // Login endpoint
      : 'http://localhost:4000/signup'; // Signup endpoint

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (!response.ok) throw new Error(data.message || 'Authentication failed');

      // Store user details in localStorage
      localStorage.setItem('auth_token', data.token);
      localStorage.setItem('userName', data.username); // Assuming the backend sends 'username'
      localStorage.setItem('userCountry', data.country || 'Unknown'); // Optional: country
      localStorage.setItem('addressCount', data.addressCount || 0); // Optional: address count
      localStorage.setItem('isLoggedIn', true); // Mark user as logged in

      console.log(`${isLogin ? 'Login' : 'Signup'} successful:`, data);

      // Redirect to Account page
      navigate('/account');
    } catch (error) {
      console.error(`Error during ${isLogin ? 'login' : 'signup'}:`, error.message || error);
      alert(error.message || 'Something went wrong. Please try again.');
    }
  };

  return (
    <div className="login-signup-container">
      <div className="form-wrapper">
        <h2>{isLogin ? 'Login' : 'Sign Up'}</h2>
        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <>
              <label htmlFor="name">Full Name</label>
              <input
                type="text"
                id="name"
                name="name"
                placeholder="Enter your name"
                value={formData.name}
                onChange={handleChange}
              />
            </>
          )}
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            placeholder="Enter your email"
            value={formData.email}
            onChange={handleChange}
          />
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            placeholder="Enter your password"
            value={formData.password}
            onChange={handleChange}
          />
          <button type="submit">{isLogin ? 'Login' : 'Sign Up'}</button>
        </form>
        <p onClick={toggleForm} className="toggle-text">
          {isLogin
            ? "Don't have an account? Sign up"
            : 'Already have an account? Login'}
        </p>
      </div>
    </div>
  );
};
