import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./ProductDetails.css";

// Importing product images
import evilEyeBracelet from '../Components/Assets/a1.png'; 
import TropicalRainbowAnklet from '../Components/Assets/a2.png'; 
import boldbeads from '../Components/Assets/a3.png'; 
import OceanAuraAnklets from '../Components/Assets/a4.png'; 
import multicolour from '../Components/Assets/a5.png'; 

const ProductDetails = () => {
  const { productId } = useParams(); // Extract the product ID from the URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Mock product data
    const mockData = [
      {
        id: 1,
        name: "Evil Eye Bracelet",
        price: 250,
        originalPrice: 500,
        image: evilEyeBracelet,
        description: "Protect yourself with style using this Evil Eye Bracelet, designed to ward off negativity while adding elegance to your outfit.",
        stock: 3,
        rating: 5.0,
        reviews: 2,
        material: "Made with Pure 925 Silver",
        warranty: "6-Month Warranty",
        returnPolicy: "Easy 30 Day Return",
        additional: "Add gift wrap to your order (₹50)",
      },
      {
        id: 2,
        name: "Tropical Rainbow Anklet",
        price: 300,
        originalPrice: 600,
        image: TropicalRainbowAnklet,
        description: "Feel the tropical vibes with this colorful Rainbow Anklet, perfect for sunny days and beach adventures.",
        stock: 5,
        rating: 4.8,
        reviews: 5,
        material: "Made with Pure 925 Silver",
        warranty: "6-Month Warranty",
        returnPolicy: "Easy 30 Day Return",
        additional: "Add gift wrap to your order (₹50)",
      },
      // Add other mock products here
    ];

    // Find the product by ID
    const productData = mockData.find((item) => item.id === parseInt(productId));
    setProduct(productData);
  }, [productId]);

  // Display a loading message while product data is being fetched
  if (!product) {
    return <div>Loading product details...</div>;
  }

  // Render product details
  return (
    <div className="product-details-container">
      <h1>{product.name}</h1>
      <img src={product.image} alt={product.name} className="product-details-image" />
      <p className="price">₹{product.price} <span className="original-price">₹{product.originalPrice}</span></p>
      <p className="material">{product.material}</p>
      <p className="stock">Hurry Up! Only <span className="highlight">{product.stock} Item(s)</span> left in stock</p>
      <p className="rating">⭐ {product.rating} ({product.reviews} reviews)</p>

      <div className="purchase-options">
        <button>Pair</button>
        <button>Single</button>
      </div>

      <div className="delivery-time">
        <p>Estimated Delivery Time</p>
        <input type="text" placeholder="Enter 6 digit pincode" />
        <button>Check</button>
      </div>

      <div className="benefits">
        <p>{product.returnPolicy}</p>
        <p>{product.warranty}</p>
      </div>

      <div className="additional">
        <p>{product.additional}</p>
      </div>

      <div className="action-buttons">
        <button className="buy-now">Buy Now</button>
        <button className="add-to-cart">Add To Cart</button>
      </div>
    </div>
  );
};

export default ProductDetails;
