import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const ProductDetails = () => {
  const { id } = useParams(); // Get the product ID from the URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Replace this with a real API call if you have a backend
    const mockData = [
      {
        id: 1,
        name: "Evil Eye Bracelet",
        price: 250,
        originalPrice: 500,
        description: "A stunning bracelet to ward off negativity.",
        image: "https://via.placeholder.com/300",
      },
      {
        id: 2,
        name: "Tropical Rainbow Anklet",
        price: 300,
        originalPrice: 600,
        description: "Bright and colorful anklet for tropical vibes.",
        image: "https://via.placeholder.com/300",
      },
    ];

    const productData = mockData.find((item) => item.id === parseInt(id));
    setProduct(productData);
  }, [id]);

  if (!product) {
    return <div>Loading product details...</div>;
  }

  return (
    <div className="product-details">
      <h1>{product.name}</h1>
      <img src={product.image} alt={product.name} />
      <p>Price: ₹{product.price}</p>
      <p style={{ textDecoration: "line-through" }}>
        Original Price: ₹{product.originalPrice}
      </p>
      <p>{product.description}</p>
      <button>Add to Cart</button>
    </div>
  );
};

export default ProductDetails;
