import React, { useState } from 'react';
import './Navbar.css';
import logo from '../Assets/logo.png'; // Ensure this path is correct
import cart from '../Assets/cart.png'; // Ensure this path is correct
import heart from '../Assets/heart.png'; // Add your wishlist icon path here
import { Link } from 'react-router-dom';

export const Navbar = () => {
  const [menu, setMenu] = useState('shop'); // Tracks the active menu item

  return (
    <div className="navbar">
      {/* Logo Section */}
      <div className="nav-logo">
        <img src={logo} alt="Logo" />
        <p>SHOPPER</p>
      </div>

      {/* Navigation Menu */}
      <ul className="nav-menu">
        <li onClick={() => setMenu('shop')}>
          <Link style={{ textDecoration: 'none' }} to="/">
            Shop
          </Link>
          {menu === 'shop' && <hr />} {/* Highlight active menu */}
        </li>
        <li onClick={() => setMenu('mens')}>
          <Link style={{ textDecoration: 'none' }} to="/mens">
            Mens
          </Link>
          {menu === 'mens' && <hr />}
        </li>
        <li onClick={() => setMenu('womens')}>
          <Link style={{ textDecoration: 'none' }} to="/womens">
            Womens
          </Link>
          {menu === 'womens' && <hr />}
        </li>
        <li onClick={() => setMenu('kids')}>
          <Link style={{ textDecoration: 'none' }} to="/kids">
            Kids
          </Link>
          {menu === 'kids' && <hr />}
        </li>
      </ul>

      {/* Wishlist, Cart, and Login Section */}
      <div className="nav-login-cart">
        {/* Wishlist */}
        <Link to="/wishlist" className="nav-wishlist">
        <img src={heart} alt="heart" />
          <span>Wishlist</span>
        </Link>

        {/* Cart */}
        <Link to="/cart" className="nav-cart">
          <img src={cart} alt="Cart" />
        </Link>
        <div className="nav-cart-count">0</div> {/* Placeholder for cart count */}

        {/* Login */}
        <Link to="/login">
          <button>Login</button>
        </Link>
      </div>
    </div>
  );
};
