import React from "react";
import "./Account.css"; // Import the corresponding CSS file

const Account = ({ username, country, orders }) => {
  return (
    <div className="account-container">
      <div className="account-left">
        <h2>Account</h2>
        <h3>Order history</h3>
        <p>
          {orders && orders.length > 0
            ? orders.map((order, index) => <div key={index}>{order}</div>)
            : "You haven't placed any orders yet."}
        </p>
        <a href="/logout" className="logout-link">
          Log out
        </a>
      </div>
      <div className="account-right">
        <h3>Account details</h3>
        <p>{username}</p>
        <p>{country}</p>
        <a href="/addresses" className="view-address-link">
          View addresses (1)
        </a>
      </div>
    </div>
  );
};

export default Account;
